import { useEffect, useState } from "react";
import { MapContainer, TileLayer, CircleMarker, Popup, Circle, useMap } from "react-leaflet";
import "leaflet/dist/leaflet.css";

// Componente para cambiar la vista del mapa al hacer clic en el marcador
const ChangeView = ({ coordenadas }) => {
  const map = useMap();

  useEffect(() => {
    if (coordenadas) {
      map.setView([coordenadas.lat, coordenadas.lng], 7); // Mueve el mapa
    }
  }, [coordenadas, map]);

  return null;
};

const MapaMexico = ({ coordenadas }) => {
  const [circleScale, setCircleScale] = useState(0);
  const [opacity, setOpacity] = useState(0.6);

  useEffect(() => {
    if (coordenadas) {
      setCircleScale(1); // Comienza el efecto de la onda
      setOpacity(0.6);   // Empieza con la opacidad normal

      const interval = setInterval(() => {
        setCircleScale((prev) => prev + 0.1); // Aumenta el tamaño del círculo
        setOpacity((prev) => prev - 0.05);   // Disminuye la opacidad

        // Detener la animación cuando el círculo sea demasiado grande o casi invisible
        if (circleScale > 2 || opacity <= 0) {
          clearInterval(interval);
          setCircleScale(0);
          setOpacity(0.6);
        }
      }, 100); // Cada 100ms para actualizar la animación

      return () => clearInterval(interval); // Limpiar el intervalo cuando el componente se desmonte
    }
  }, [coordenadas, circleScale, opacity]);

  return (
    <MapContainer
      center={[23.6345, -102.5528]} // Centro inicial en México
      zoom={5}
      style={{ height: "100vh", width: "100%" }}
    >
      <TileLayer
        url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
        attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
        noWrap={true}
      />
      <ChangeView coordenadas={coordenadas} />

      {/* Muestra un CircleMarker solo si hay coordenadas */}
      {coordenadas && (
        <>
          <CircleMarker
            center={[coordenadas.lat, coordenadas.lng]}
            radius={10} // Tamaño del círculo
            color="red"  // Color del círculo
            fillColor="red" // Color del relleno
            fillOpacity={0.6} // Opacidad del relleno
          >
            <Popup>
              <b>Ubicación del Sismo</b> <br />
              Lat: {coordenadas.lat}, Lng: {coordenadas.lng}
            </Popup>
          </CircleMarker>

          {/* Onda sísmica (expansión) */}
          <Circle
            center={[coordenadas.lat, coordenadas.lng]}
            radius={circleScale * 100} // Escala de expansión
            color="red"
            fillColor="red"
            fillOpacity={opacity} // Opacidad decreciente
            stroke={false} // Sin borde
          />
        </>
      )}
    </MapContainer>
  );
};

export default MapaMexico;
