import { useEffect } from "react";
import { MapContainer, TileLayer, useMap } from "react-leaflet";
import "leaflet/dist/leaflet.css";

const ChangeView = ({ coordenadas }) => {
  const map = useMap();

  useEffect(() => {
    if (coordenadas) {
      map.setView([coordenadas.lat, coordenadas.lng], 7); // Centra el mapa
    }
  }, [coordenadas, map]);

  return null;
};

const MapaMexico = ({ coordenadas }) => {
  return (
    <MapContainer
      center={[23.6345, -102.5528]} // Centro inicial de México
      zoom={5}
      style={{ height: "100vh", width: "100%" }}
    >
      <TileLayer
        url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
        attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
        noWrap={true}
      />
      <ChangeView coordenadas={coordenadas} />
    </MapContainer>
  );
};

export default MapaMexico;
