import { useEffect, useState } from "react";
import "./components/Inicio.css";  // Asegúrate de crear este archivo para los estilos

function Inicio() {
  const [sismos, setSismos] = useState([]);

  useEffect(() => {
    fetch("http://localhost:8080/inicio", {
      method: "GET",
      headers: {
        "Content-Type": "application/json",
      }
    })
      .then((response) => {
        if (!response.ok) throw new Error("Error en la respuesta del servidor");
        return response.json();
      })
      .then((data) => {
        console.log("Datos obtenidos: ", data);
        setSismos(data);
      })
      .catch((error) => console.error("Error al obtener datos:", error));
  }, []);

  return (
    <div className="container">
      {/* Menú lateral */}
      <div className="sidebar">
        <h2>Menú</h2>
        <ul>
          <li><a href="#sismos">Sismos Recientes</a></li>
          {/* Puedes agregar más opciones aquí */}
        </ul>
      </div>

      {/* Contenido principal */}
      <div className="content">
        <h2 id="sismos">Sismos Recientes</h2>
        <ul className="sismos-list">
          {sismos.map((sismo) => (
            <li key={sismo.identificador} className="sismo-item">
              <div className="sismo-details">
                <strong>{sismo.referenciaLocalizacion}</strong> - Magnitud: {sismo.magnitud}
                <div>
                  <b>Fecha:</b> {sismo.fecha} {sismo.hora}
                </div>
                <div>
                  <b>Profundidad:</b> {sismo.profundidad} km
                </div>
                <div>
                  <b>Ubicación:</b> Latitud: {sismo.latitud}, Longitud: {sismo.longitud}
                </div>
                <div>
                  <b>Estatus:</b> {sismo.estatus}
                </div>
              </div>
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
}

export default Inicio;
