import { useEffect, useState } from "react";

function Inicio() {
  const [sismos, setSismos] = useState([]);

  useEffect(() => {
    // Hacemos la solicitud GET para obtener los sismos
    fetch("http://localhost:8080/inicio", {
      method: "GET",
      headers: {
        "Content-Type": "application/json",  // Establece el tipo de contenido como JSON
      }
    })
      .then((response) => {
        if (!response.ok) throw new Error("Error en la respuesta del servidor");
        return response.json();
      })
      .then((data) => {
        console.log("Datos obtenidos: ", data);  // Agrega un log para verificar la estructura de los datos
        setSismos(data);  // Actualiza el estado con los datos recibidos
      })
      .catch((error) => console.error("Error al obtener datos:", error));  // Captura errores
  }, []);

  return (
    <div>
      <h2>Sismos Recientes</h2>
      <ul>
        {/* Itera sobre los sismos y los muestra */}
        {sismos.map((sismo) => (
          <li key={sismo.identificador}>
            <strong>{sismo.referenciaLocalizacion}</strong> - Magnitud: {sismo.magnitud}  
            <br /> Fecha: {sismo.fecha} {sismo.hora}  
            <br /> Profundidad: {sismo.profundidad} km  
            <br /> Latitud: {sismo.latitud}, Longitud: {sismo.longitud}  
            <br /> Estatus: {sismo.estatus}  
          </li>
        ))}
      </ul>
    </div>
  );
}

export default Inicio;
